mapper.insert:
INSERT INTO menus
SET id = #{id}
,   store_id = #{storeId}
,   name = #{name}
,   comment =#{comment}
,   price = #{price}
,   image_path = #{imagePath}

mapper.select:
SELECT id AS menuId, store_id, name, comment, price, image_path
FROM menus
WHERE id = #{menuId}

mapper.update:
UPDATE menus
SET name = #{name}
,   comment =#{comment}
,   price = #{price}
,   image_path = #{imagePath}
WHERE id = #{menuId}
