mapper.insert:
INSERT INTO stores
SET user_id = #{userId}
, category = #{category}
, name = #{name}
, comment = #{comment}
, business_number = #{businessNumber}
, license_path = #{licensePath}
, address = #{address}
, tel = #{tel}
, owner_name = #{ownerName}

mapper.select:
SELECT s.id AS storeId, s.name, s.category, s.is_active , COUNT(DISTINCT f.user_id, f.store_id) AS favorites , IFNULL(AVG(r.rating), 0) AS rating FROM stores s LEFT JOIN favorites f ON s.id = f.store_id LEFT JOIN orders o ON s.id = o.store_id LEFT JOIN reviews r ON o.id = r.order_id
s.name LIKE '%${searchText}%'
AND s.category = #{category}
GROUP BY s.id;

mapper.update:
UPDATE stores

    category = #{category}
    , name = #{name}
    , comment = #{comment}
    , business_number = #{businessNumber}
    , license_path = #{licensePath}
    , address = #{address}
    , tel = #{tel}
    , owner_name = #{ownerName}

        , image_path = #{imagePath}


WHERE id = #{storeId}
AND user_id = #{userId}

UPDATE stores
SET is_active = IF(is_active = 1, 0, 1)
WHERE id = #{storeId}
AND user_id = #{userId}

mapper.delete:
DELETE FROM stores
WHERE id = #{storeId}
AND user_id = #{userId}
