mapper.insert:
INSERT INTO stores
SET user_id = #{userId}
, category = #{category}
, name = #{name}
, comment = #{comment}
, business_number = #{businessNumber}
, license_path = #{licensePath}
, address = #{address}
, owner_name = #{ownerName}

mapper.select:
SELECT s.id AS storeId, s.name, s.category , COUNT(DISTINCT f.user_id, f.store_id) AS likeCount , IFNULL(AVG(r.rating), 0) AS avgRating FROM stores s LEFT JOIN favorites f ON s.id = f.store_id LEFT JOIN orders o ON s.id = o.store_id LEFT JOIN reviews r ON o.id = r.order_id
s.name LIKE '%${searchText}%'
s.category = #{categoryId}
GROUP BY s.id;
